import React, { useContext } from 'react';
import './App.css';
import Cards from './Components/Cards';
import Navs from './Components/Navs';
import LeftNav from './Components/LeftNav';
import Banner from './Components/Banner';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import { BrowserRouter as Router, Switch, Route, useLocation } from 'react-router-dom';
import { __RouterContext } from 'react-router';
import { useTransition, animated } from 'react-spring';

function App() {
  //setting up array for transitions using react-spring
  //location is an object with information that will be passed to Switch below
  const locations = useLocation();
  //passing the objects into an array of 'transitions' to iterate
  const transitions = useTransition(locations, location => location.pathname, {
    from: { opacity: 0, transform: "translate(100%, 0)" },
    enter: { opacity: 1, transform: "translate(0%, 0)" },
    leave: { opacity: 0, transform: "translate(-50%, 0)" }
  });


  return (
    <Router>
      <div className="App">
        <Navs />
        <Container>
          <Row>
            {/* <!-- Left Side --> */}
            <Col md="3" >
              <LeftNav />
            </Col>

            <Col md="9">
            {transitions.map(({ item, props, key }) => (
              <animated.div key={key} style={props}>
                <Switch locations={item}>
                  {/* <!-- Right Side --> */}

                    {/* Top right side */}
                    <Route exact path='/Cards' component={Cards} />
                    <Route exact path='/Banner' component={Banner} />



                    {/* Bottom right side */}


                </Switch>
              </animated.div>
            ))}

</Col>

          </Row> {/* <!-- End of Row --> */}
          </Container> {/* <!-- End of Container --> */}
      </div> {/* <!-- End of App --> */}
    </Router>
  );
}

export default App;
