//animation settings for framer motion to apply to components in the app


export const pageVariants = {
    in: {
      opacity: 1,
      x: 0,
      scale: 1
    },
    out: {
      opacity: 0,
      x: "-100vw",
      scale: 0.8
    }
  };


export const pageTransition = {
    type: 'spring',
    ease: 'anticipate',
    duration: 0.5
};
