import React from "react";
import { Editor } from "@tinymce/tinymce-react";
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import { BsCloudUpload } from 'react-icons/bs';
import { FiSave } from 'react-icons/fi';


// Using class based component as there was issues with capturing through hooks
// Will later look into why 
class Entry extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            formData: {
                content: "" ,
                headline: ""
            }
        };
        this.handleHeadlineChange = this.handleHeadlineChange.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleChange(event) {
        this.setState({formData: {
            content: event.target.value,
            headline: this.state.formData.headline
        }});
    }

    handleHeadlineChange(event) {
        this.setState({formData: {
            content: this.state.formData.content,
            headline: event.target.value
        }});
    }

    handleSubmit(event) {
        alert("Text was submitted: " + this.state.formData.content);
        event.preventDefault();
    }

    render() {
        return (
            <div>
                <div>
                    <Form className="mb-2">
                        <Row>
                            <Col>
                                <Form.Control placeholder="Headline" />
                            </Col>
                        </Row>
                    </Form>
                </div>

                <form onSubmit={this.handleSubmit}>
                    <Editor
                        apiKey="3rc40kpnnv698neatb34c8fw2zfctnavcegd49sri6ix9s2d"
                        value={this.state.content}
                        init={{
                            height: 500,
                            menubar: false,
                            plugins: [
                                'advlist autolink lists link image charmap print preview anchor',
                                'searchreplace visualblocks code fullscreen',
                                'insertdatetime media table paste code help wordcount'
                            ],
                            toolbar: 'undo redo | formatselect | ' +
                                'bold italic backcolor | alignleft aligncenter ' +
                                'alignright alignjustify | bullist numlist outdent indent | ' +
                                'removeformat | help',
                            content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                        }}
                        onEditorChange={this.handleChange}
                    />
                    <br />
                    <Button className="mb-2" variant="outline-primary" type="submit"><FiSave size={24} /></Button>
                    <Button className="mb-2 ml-3" variant="outline-secondary"><BsCloudUpload size={24} /></Button>
                </form>
            </div>
        );
    }
}

export default Entry;
