import React, {useState} from "react";
import { Editor } from "@tinymce/tinymce-react";
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import { BsCloudUpload } from 'react-icons/bs';
import { FiSave } from 'react-icons/fi';


// Using class based component as there was issues with capturing through hooks
// Will later look into why 
const Entry = () => {

    const [contentEditor, setContentEditor] = useState("");

    const handleEditorChange = (content, editor) => {
      console.log('Content was updated:', content);
      setContentEditor(content);
    }

        return (
            <div>
                <div>
                    <Form className="mb-2">
                        <Row>
                            <Col>
                                <Form.Control onChange={this.handleHeadlineChange} placeholder="Headline" />
                            </Col>
                        </Row>
                    </Form>
                </div>

                <form onSubmit={this.handleSubmit}>
                    <Editor
                        apiKey="3rc40kpnnv698neatb34c8fw2zfctnavcegd49sri6ix9s2d"
                        value={this.state.content}
                        init={{
                            height: 500,
                            menubar: false,
                            plugins: [
                                'advlist autolink lists link image charmap print preview anchor',
                                'searchreplace visualblocks code fullscreen',
                                'insertdatetime media table paste code help wordcount'
                            ],
                            toolbar: 'undo redo | formatselect | ' +
                                'bold italic backcolor | alignleft aligncenter ' +
                                'alignright alignjustify | bullist numlist outdent indent | ' +
                                'removeformat | help',
                            content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                        }}
                        onEditorChange={handleEditorChange}
                    />
                    <br />
                    <Button className="mb-2" variant="outline-primary" type="submit"><FiSave size={24} /></Button>
                    <Button className="mb-2 ml-3" variant="outline-secondary"><BsCloudUpload size={24} /></Button>
                </form>
            </div>
        );

}

